              'Expected "proof.verificationMethod" to exist.'
            );
            proof.verificationMethod.should.be.a(
              'string',
              'Expected "proof.verificationMethod" to be a string.'
            );
            const [publicKey] = proof.verificationMethod.split('#');
            checkEncoding({
